import { useContext } from 'react';
import incomeImg from '../../assets/entrada.svg'
import saidaImg from '../../assets/saida.svg'
import totalImg from '../../assets/total.svg'
import { Container } from "./styles";
import { TransactionContext } from '../../transaction_context';


export function Summary(){

    const {transactions} = useContext(TransactionContext)
    const totalDeposit = transactions.reduce((acc, transaction) => {
        if(transaction.type == 'deposit') {
            return acc + transaction.amount;
        }
        return acc;
    }, 0);

    const subDeposit = transactions.reduce((acc, transaction) => {
        if(transaction.type == 'withdraw'){
            return acc - transaction.amount;
        }
        return acc;
    }, 0)
    return(
        <Container>
            <div>
                <header>
                    <p>Entradas</p>
                    <img src={incomeImg} alt="Entrada" />
                </header>
                <strong>R${totalDeposit}</strong>
            </div>
            <div>
                <header>
                    <p>Saidas</p>
                    <img src={saidaImg} alt="Saidas" />
                </header>
                <strong>R${subDeposit}</strong>
            </div>
            <div className='highlight'>
                <header>
                    <p>Total</p>
                    <img src={totalImg} alt="Total" />
                </header>
                <strong>R$1000,00</strong>
            </div>
        </Container>
    )
}
